        return new Source("{% extends 'base.html.twig' %}

{% block title %}Les produits
{% endblock %}

{% block body %}

    <div class=\"row\">
        {% for product in products %}
            <div class=\"col-4 mt-4\">
                <div class=\"border border-dark rounded p-2 clearfix\">
                    <h2>
                        {{ product.name }}
                        -
                        {{ product.price }}
                        €
                    </h2>

                    <img  style=\"height: 230px\" class=\"img-fluid rounded \" src=\"{{ product.photo }}\" alt=\"Image de {{ product.name }}\">

                    <hr>
                    {{product.description}}
                    <hr>
                    <a href=\"{{ path('cart_add', {'id': product.id})}}\" class=\"btn btn-success float-right\">
                        <i class=\"fas fa-shopping-cart mr-1\"></i>
                        Ajouter
                    </a>
                </div>

            </div>
        {% endfor %}
    </div>
{% endblock %}

", "shop/index.html.twig", "/home/leo/Bureau/T-WEB-600-LIL-6-1-ecommerce-romain.noizet/app/templates/shop/index.html.twig");
